mapper.select:
select

from lims_case_info lci, lims_consignment lc
where lc.CASE_ID=lci.ID and lc.ID = #{id,jdbcType=INTEGER}

SELECT substring(lci.CASE_NO, 5, 4) as caseNo1, lpad(substring_index(lci.CASE_NO, '-', -1),4,'0000') as caseNo2, , lc.ID as CONSIGNMENT_ID, lc.ENTRUSTING_SERIAL, lc.DELEGATE_ORG, lc.DELEGATE_DATETIME, lc.DELEGATOR1, lc.DELEGATOR2, lc.ADDITIONAL_FLAG, lc.ACCEPTOR, lc.ACCEPT_DATETIME, lc.REFUSE_REASON as REFUSE_REASON, di_1.dict_name as CASE_TYPE_NAME, di_2.dict_name as CASE_PROPERTY_NAME, di_3.dict_name as CASE_LEVEL_NAME, di_4.dict_name as CASE_STATUS_NAME, do.org_name as DELEGATE_ORG_NAME from lims_consignment lc left join delegate_org do on do.org_code=lc.delegate_org, lims_case_info lci left join dict_item di_1 on lci.CASE_TYPE=di_1.dict_code and di_1.DICT_TYPE_CODE='CASE_TYPE' left join dict_item di_2 on lci.CASE_PROPERTY=di_2.dict_code and di_2.DICT_TYPE_CODE='CASE_PROPERTY' left join dict_item di_3 on lci.CASE_LEVEL=di_3.dict_code and di_3.DICT_TYPE_CODE='CASE_LEVEL' left join dict_item di_4 on lci.CASE_STATUS=di_4.dict_code and di_4.DICT_TYPE_CODE='CASE_STATUS' where lci.ID=lc.CASE_ID and lci.DELETE_FLAG='0' and lc.DELETE_FLAG='0' and lci.ENTRUSTMENT_TYPE is null
order by ${orderByClause}
order by lc.CREATE_DATETIME DESC
limit #{offset}, #{rows}
order by lc.CREATE_DATETIME DESC

SELECT substring(lci.CASE_NO, 5, 4) as caseNo1, lpad(substring_index(lci.CASE_NO, '-', -1),4,'0000') as caseNo2, , lc.ID as CONSIGNMENT_ID, lc.ENTRUSTING_SERIAL, lc.DELEGATE_ORG, lc.DELEGATE_DATETIME, lc.DELEGATOR1, lc.DELEGATOR2, lc.ADDITIONAL_FLAG, lc.ACCEPTOR, lc.ACCEPT_DATETIME, lc.REFUSE_REASON as REFUSE_REASON, di_1.dict_name as CASE_TYPE_NAME, di_2.dict_name as CASE_PROPERTY_NAME, di_3.dict_name as CASE_LEVEL_NAME, di_4.dict_name as CASE_STATUS_NAME, do.org_name as DELEGATE_ORG_NAME from lims_consignment lc left join delegate_org do on do.org_code=lc.delegate_org, lims_case_info lci left join dict_item di_1 on lci.CASE_TYPE=di_1.dict_code and di_1.DICT_TYPE_CODE='CASE_TYPE' left join dict_item di_2 on lci.CASE_PROPERTY=di_2.dict_code and di_2.DICT_TYPE_CODE='CASE_PROPERTY' left join dict_item di_3 on lci.CASE_LEVEL=di_3.dict_code and di_3.DICT_TYPE_CODE='CASE_LEVEL' left join dict_item di_4 on lci.CASE_STATUS=di_4.dict_code and di_4.DICT_TYPE_CODE='CASE_STATUS' where lci.ID=lc.CASE_ID and lci.DELETE_FLAG='0' and lc.DELETE_FLAG='0'
order by ${orderByClause}
order by lc.CREATE_DATETIME DESC
limit #{offset}, #{rows}
order by lc.CREATE_DATETIME DESC

SELECT substring(lci.CASE_NO, 5, 4) as caseNo1, lpad(substring_index(lci.CASE_NO, '-', -1),4,'0000') as caseNo2, , lc.ID as CONSIGNMENT_ID, lc.DELEGATE_ORG, lc.DELEGATE_DATETIME, lc.DELEGATOR1, lc.DELEGATOR2, lc.ADDITIONAL_FLAG, lc.ACCEPTOR, lc.ACCEPT_DATETIME, lc.REFUSE_REASON as REFUSE_REASON, di_1.dict_name as CASE_TYPE_NAME, di_2.dict_name as CASE_PROPERTY_NAME, di_3.dict_name as CASE_LEVEL_NAME, di_4.dict_name as CASE_STATUS_NAME, do.org_name as DELEGATE_ORG_NAME, lib.STATUS_NAME AS identifyBookStatusName, lib.STATUS as identifyBookStatus from lims_consignment lc left join delegate_org do on do.org_code=lc.delegate_org left join lims_identify_book lib on lib.CASE_ID=lc.CASE_ID, lims_case_info lci left join dict_item di_1 on lci.CASE_TYPE=di_1.dict_code and di_1.DICT_TYPE_CODE='CASE_TYPE' left join dict_item di_2 on lci.CASE_PROPERTY=di_2.dict_code and di_2.DICT_TYPE_CODE='CASE_PROPERTY' left join dict_item di_3 on lci.CASE_LEVEL=di_3.dict_code and di_3.DICT_TYPE_CODE='CASE_LEVEL' left join dict_item di_4 on lci.CASE_STATUS=di_4.dict_code and di_4.DICT_TYPE_CODE='CASE_STATUS' where lci.ID=lc.CASE_ID and lci.DELETE_FLAG='0' and lc.DELETE_FLAG='0'
order by ${orderByClause}
order by lc.CREATE_DATETIME DESC
limit #{offset}, #{rows}
order by lc.CREATE_DATETIME DESC

SELECT substring(lci.CASE_NO, 5, 4) as caseNo1, LPAD(substring_index(lci.CASE_NO, '-', -1),4,'0000') as caseNo2, , lc.ID as CONSIGNMENT_ID, lc.DELEGATE_ORG, lc.DELEGATE_DATETIME, lc.DELEGATOR1, lc.DELEGATOR2, lc.ADDITIONAL_FLAG, lc.ACCEPTOR, lc.ACCEPT_DATETIME, lc.REFUSE_REASON as REFUSE_REASON, di_1.dict_name as CASE_TYPE_NAME, di_2.dict_name as CASE_PROPERTY_NAME, di_3.dict_name as CASE_LEVEL_NAME, di_4.dict_name as CASE_STATUS_NAME, do.org_name as DELEGATE_ORG_NAME from lims_consignment lc left join delegate_org do on do.org_code=lc.delegate_org, lims_case_info lci left join dict_item di_1 on lci.CASE_TYPE=di_1.dict_code and di_1.DICT_TYPE_CODE='CASE_TYPE' left join dict_item di_2 on lci.CASE_PROPERTY=di_2.dict_code and di_2.DICT_TYPE_CODE='CASE_PROPERTY' left join dict_item di_3 on lci.CASE_LEVEL=di_3.dict_code and di_3.DICT_TYPE_CODE='CASE_LEVEL' left join dict_item di_4 on lci.CASE_STATUS=di_4.dict_code and di_4.DICT_TYPE_CODE='CASE_STATUS' where lci.ID=lc.CASE_ID
and lci.DELETE_FLAG=#{entity.deleteFlag,jdbcType=CHAR} and lc.DELETE_FLAG=#{entity.deleteFlag,jdbcType=CHAR}
order by ${orderByClause}
order by lc.CREATE_DATETIME DESC
limit #{offset}, #{rows}
order by lc.CREATE_DATETIME DESC

SELECT count(lc.ID) from lims_consignment lc, lims_case_info lci
where lci.ID=lc.CASE_ID

  and lci.DELETE_FLAG=#{entity.deleteFlag,jdbcType=CHAR} and lc.DELETE_FLAG=#{entity.deleteFlag,jdbcType=CHAR}

mapper.delete:
UPDATE lims_case_info lci
set lci.DELETE_FLAG='1',  lci.DELETE_DATETIME = now()
where lci.ID = #{id,jdbcType=INTEGER}

UPDATE lims_case_info lci left join lims_consignment lc on lci.ID=lc.CASE_ID
set lci.DELETE_FLAG='1',  lci.DELETE_DATETIME = now()
where lc.ID = #{id,jdbcType=INTEGER}

mapper.update:
update lims_case_info
set
CASE_XK_ANO = #{caseXkAno,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}

update lims_case_info
set CASE_STATUS = #{caseStatus,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}
